package JavaCurso;

public class TabuadaFor {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n = 5;
				
				System.out.println("tabuada do: " + n + ":");
		for (int i = 1; i <=10; i++) {
			int resultado = n * i;
			System.out.println(n + " x " + i + " = "+ resultado);
		}

	}

}
